package basic;

public class ForLoopEx {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		System.out.println("Getting Inside For Loop");
		
		for(int i= 20; i>=15; i--)
		{
			System.out.println("Value of i is : " +i);
		}
		
		System.out.println("Out of For Loop");

	}

}
